// Register 注册服务 // 重命名 RegisterService 为 Register func (k *MicroKernel) Register(svc Service) error { k.mu.Lock() defer k.mu.Unlock() // name 可以放在锁前面，优化性能 name := svc.Name() // 增加状态导入 // 如果使用的接口，这边就使用的接口方法 if k.stateStore != nil && k.stateStore.Exists(name) { // 查看服务是否支持状态导入 // 状态导入不要求每个服务必须实现 // 如果没有实现，就直接忽略 if importer, ok := svc.(Importable); ok { raw, err := k.stateStore.Load(name) if err != nil { return fmt.Errorf("state load failed: %w
", err) } err = importer.ImportState(raw) if err != nil { return fmt.Errorf("state import failed: %w
", err) } fmt.Printf("State migrated for service %s
", name) } } if _, ok := k.services[name]; ok { return errors.New("service already registered") } k.services[name] = &serviceMeta{ svc: svc, state: Created, deps: svc.Dependencies(), } fmt.Println("Registered:", svc.Name()) return nil }

