#include <stdio.h> #include <stdlib.h> #include <unistd.h> #include <signal.h> #include <sys/wait.h> // SIGCHLD 信号处理函数 void sigchld_handler(int signum) { // 使用 waitpid 非阻塞回收所有已终止的子进程 while (waitpid(-1, NULL, WNOHANG) > 0); } int main() { // 设置 SIGCHLD 信号处理函数 struct sigaction sa; sa.sa_handler = sigchld_handler; sigemptyset(&sa.sa_mask); sa.sa_flags = SA_RESTART; // 自动重启被中断的系统调用 if (sigaction(SIGCHLD, &sa, NULL) == -1) { perror("sigaction"); exit(EXIT_FAILURE); } // 创建子进程 pid_t pid = fork(); if (pid < 0) { perror("fork"); exit(EXIT_FAILURE); } if (pid == 0) { // 子进程 printf("子进程: PID = %d
", getpid()); sleep(10); // 模拟子进程执行一些任务 printf("子进程结束: PID = %d
", getpid()); exit(EXIT_SUCCESS); } else { // 父进程 printf("父进程: PID = %d, 子进程 PID = %d
", getpid(), pid); // 父进程继续执行其他任务 for (int i = 0; i < 5; ++i) { printf("父进程正在工作...
"); sleep(10000) } // 父进程结束前的收尾工作 printf("父进程结束: PID = %d
", getpid()); } return 0; }

